        public static string MapVisualStudioPath(string path)
        {
            string mappedPath = System.IO.Path.GetDirectoryName(System.Reflection.Assembly.GetExecutingAssembly().Location);
            mappedPath = System.IO.Path.Combine(mappedPath, "../..");
            mappedPath = System.IO.Path.GetFullPath(mappedPath);

            if (path.StartsWith("~"))
                return mappedPath + path.Substring(1);

            return path;
        } // End Function MapVisualStudioPath


        private void button1_Click(object sender, System.EventArgs e)
        {
            string hex = FileToHexString(@"~\images\SingleTransparentPixel.png");
            System.Windows.Forms.Clipboard.SetText(hex);
            System.Console.WriteLine(hex);
        } // End Sub button1_Click


        public static string FileToHexString(string filename)
        {
            filename = MapVisualStudioPath(filename);
            byte[] data = System.IO.File.ReadAllBytes(filename);
            return ImageToHexString(data);
        } // End Function FileToHexString


