        public static void HexStringToFile()
        {
            string str = @"";

            byte[] array = StringToByteArrayFastest(str);
            System.IO.File.WriteAllBytes(@"d:\NoImage.jpg", array);
        } // End Function HexStringToFile


        // http://stackoverflow.com/questions/321370/how-can-i-convert-a-hex-string-to-a-byte-array
        public static byte[] StringToByteArrayFastest(string hex)
        {
            if (hex.Length % 2 == 1)
                throw new System.Exception("The binary key cannot have an odd number of digits");

            hex = hex.ToUpperInvariant();
            if (hex.StartsWith("0X"))
                hex = hex.Substring(2);


            byte[] arr = new byte[hex.Length >> 1];

            for (int i = 0; i < hex.Length >> 1; ++i)
            {
                arr[i] = (byte)((GetHexVal(hex[i << 1]) << 4) + (GetHexVal(hex[(i << 1) + 1])));
            } // Next i 

            return arr;
        } // End Function StringToByteArrayFastest 


        public static int GetHexVal(char hex)
        {
            int val = (int)hex;
            //For uppercase A-F letters:
            return val - (val < 58 ? 48 : 55);
            //For lowercase a-f letters:
            //return val - (val < 58 ? 48 : 87);
            //Or the two combined, but a bit slower:
            //return val - (val < 58 ? 48 : (val < 97 ? 55 : 87));
        } // End Function GetHexVal 


